import { QInput, QPageSticky, QPopupProxy, QDate } from 'quasar';

export default {
  components: {
    QInput,
    QPageSticky,
    QPopupProxy,
    QDate
  },
  data() {
    return {
      abrir_cadastro: false,
      cadastro_pet: {},
      meus_pets: []
    }
  },

  created () {
    this.buscarPet()
  },

  methods: {
    cadastrarNovoPet () {
      abrir_cadastro = true
    },
    pegarAInicialDoOPrimeiroEUltimoNome (texto) {
      if (texto) {
        let texto2 = '';
        texto2 = texto.toString().trim();
        if (texto2.indexOf(' ') >= 0) {
          let textos = [];
          textos = texto2.split(' ');
          let qtdTextos = 0;
          qtdTextos = textos.length;
          const iniciais = textos[0][0] + textos[qtdTextos - 1][0];
          return (iniciais).toUpperCase()
        } else {
          return ((texto2[0] + texto2[1]).toUpperCase())
        }
      }
    },

    buscarPet() {
      this.$q.loading.show()
      Meteor.call('buscarPets', (error,result) => {
        if(error) {
          this.$q.notify({
            progress: true,
            message: error.reason,
            type: 'error',
            color: 'red',
            timeout: 3500,
            multiLine: false,
            icon: 'error'
          })
        } else {
          console.log(result)
         this.meus_pets = result;
        }
        this.$q.loading.hide()
      })
    },

    salvarPet() {
      console.log(this.cadastro_pet)
      Meteor.call('adicionarPet', this.cadastro_pet, (error,result) => {
        if(error) {
          this.$q.notify({
            progress: true,
            message: error.reason,
            type: 'error',
            color: 'red',
            timeout: 3500,
            multiLine: false,
            icon: 'error'
          })
        } else {
          this.$q.notify({
            progress: true,
            message: 'Cadastrado com sucesso',
            type: 'success',
            color: 'green',
            timeout: 3500,
            multiLine: false,
            icon: 'check'
          })
          this.buscarPet()
        }
        this.abrir_cadastro = false
      })
    },

     editarPet() {
      //pet precisa ter ID, mas é pra ir o objeto inteiro
      Meteor.call('editarPet', this.pet, (error,result) => {
        if(error) {
          this.$q.notify({
            progress: true,
            message: error.reason,
            type: 'error',
            color: 'red',
            timeout: 3500,
            multiLine: false,
            icon: 'error'
          })
        } else {
          this.$q.notify({
            progress: true,
            message: 'Editado com sucesso',
            type: 'success',
            color: 'green',
            timeout: 3500,
            multiLine: false,
            icon: 'check'
          })
        }
        //FECHAR MODAL AQUI
        //RECARREGAR TELA
      })
    },

    excluirPet() {
      //só o id é suficiente
      Meteor.call('editarPet', this.pet._id, (error,result) => {
        if(error) {
          this.$q.notify({
            progress: true,
            message: error.reason,
            type: 'error',
            color: 'red',
            timeout: 3500,
            multiLine: false,
            icon: 'error'
          })
        } else {
          this.$q.notify({
            progress: true,
            message: 'Excluído com sucesso',
            type: 'success',
            color: 'green',
            timeout: 3500,
            multiLine: false,
            icon: 'check'
          })
        }
