import {
  QList,
  QItem,
  QImg
} from 'quasar';
import VisualizarProduto from './VisualizarProduto'

export default {
  data() {
    return {
      states: [
        { value: 'AC', label: 'Acre' },
        { value: 'AL', label: 'Alagoas' },
        { value: 'AP', label: 'Amapá' },
        { value: 'AM', label: 'Amazonas' },
        { value: 'BA', label: 'Bahia' },
        { value: 'CE', label: 'Ceará' },
        { value: 'DF', label: 'Distrito Federal' },
        { value: 'ES', label: 'Espírito Santo' },
        { value: 'GO', label: 'Goías' },
        { value: 'MA', label: 'Maranhão' },
        { value: 'MT', label: 'Mato Grosso' },
        { value: 'MS', label: 'Mato Grosso do Sul' },
        { value: 'MG', label: 'Minas Gerais' },
        { value: 'PA', label: 'Pará' },
        { value: 'PB', label: 'Paraíba' },
        { value: 'PR', label: 'Paraná' },
        { value: 'PE', label: 'Pernambuco' },
        { value: 'PI', label: 'Piauí' },
        { value: 'RJ', label: 'Rio de Janeiro' },
        { value: 'RN', label: 'Rio Grande do Norte' },
        { value: 'RS', label: 'Rio Grande do Sul' },
        { value: 'RO', label: 'Rondônia' },
        { value: 'RR', label: 'Roraíma' },
        { value: 'SC', label: 'Santa Catarina' },
        { value: 'SP', label: 'São Paulo' },
        { value: 'SE', label: 'Sergipe' },
        { value: 'TO', label: 'Tocantins' },
      ],
      filter: {
        bairro: '',
        cidade: '',
        uf: '',
        nome: '',
        especies: []
      },
      liberarTela: false,
      tipoUsuario: 1,
      abrirPopupCadastro: false,
      abrirPopupVisualizar: false,
      produto: {},
      opcoesEspecies: [
        {
          label: 'Cães',
          value: 'dog'
        },
        {
          label: 'Gatos',
          value: 'cat'
        },
        {
          label: 'Peixes',
          value: 'fish'
        },
        {
          label: 'Aves',
          value: 'bird'
        },
        {
          label: 'Roedores',
          value: 'hamster'
        },
        {
          label: 'Répteis',
          value: 'reptile'
        },
        {
          label: 'Outros',
          value: 'others'
        }
      ],
      produtos: [],
    }
  },
  components: {
    QList,
    QItem,
    QImg,
    VisualizarProduto
  },

  mounted() {
    this.tipoUsuario = this.$store.state.user.user.profile.tipo
  },

  async created () {
    await this.buscarProdutos()
  },

  methods: {
    pegarAInicialDoOPrimeiroEUltimoNome (texto) {
      if (texto) {
        let texto2 = '';
        texto2 = texto.toString().trim();
        if (texto2.indexOf(' ') >= 0) {
          let textos = [];
          textos = texto2.split(' ');
          let qtdTextos = 0;
          qtdTextos = textos.length;
          const iniciais = textos[0][0] + textos[qtdTextos - 1][0];
          return (iniciais).toUpperCase()
        } else {
          return ((texto2[0] + texto2[1]).toUpperCase())
        }
      }
    },

    async buscarProdutos() {
      this.$q.loading.show()
      console.log('async buscarProduto', this.filter)
      await Meteor.call('buscarProdutos', this.filter, (error,result)=>{
        if(error) {
          this.$q.notify({
            progress: true,
            message: error.reason,
            type: 'error',
            color: 'red',
            timeout: 3500,
            multiLine: false,
            icon: 'error'
          })
          console.log(error, 'error')
        } else {
          this.produtos = result.produtos
          this.fornecedores = result.fornecedores
        }
        this.liberarTela = true
        this.$q.loading.hide()
      })
      console.log(this.produtos)
    },

    excluirProduto (opcao) {
      console.log(opcao, 'opcao')
      //passar o _id do produto
      Meteor.call('excluirProduto', opcao._id, (error,result)=>{
        if(error) {
          this.$q.notify({
            progress: true,
            message: error.reason,
            type: 'error',
            color: 'red',
            timeout: 3500,
            multiLine: false,
            icon: 'error'
          })
        } else {
          this.$q.notify({
            progress: true,
            message: 'Excluido com Sucesso',
            type: 'success',
            color: 'green',
            timeout: 3500,
            multiLine: false,
            icon: 'check'
          })
          this.abrirPopupVisualizar = false,
          this.buscarProdutos()
        }
        })
    },

    editar (opcao) {
      //passar o produto todo
      //{
      // todosOsDados
      //}
      Meteor.call('editarProduto', opcao, (error,result)=>{
          if(error) {
            this.$q.notify({
              progress: true,
              message: error.reason,
              type: 'error',
              color: 'red',
              timeout: 3500,
              multiLine: false,
              icon: 'error'
            })
          } else {
            this.$q.notify({
              progress: true,
              message: 'Editado com Sucesso',
              type: 'success',
              color: 'green',
              timeout: 3500,
              multiLine: false,
              icon: 'check'
            })
          }
          this.produto = {}
          this.abrirPopupCadastro = false
          this.abrirPopupVisualizar = false
        });

    },

    showPopup (opcao) {
      this.$store.commit('produto/SET_PRODUTO', opcao)
      this.abrirPopupVisualizar = true
    },

    showPoupCadastro (opcao) {
      if (opcao?._id) {
        this.produto = opcao
      }
      this.abrirPopupCadastro = true
    },

    retornarLabel () {
      return this.produto._id ? 'Alterar' : 'Cadastrar'
    },

    cadastrarProduto () {
        Meteor.call('cadastrarProduto',this.produto,(error,result)=>{
          if(error) {
            this.$q.notify({
              progress: true,
              message: error.reason,
              type: 'error',
              color: 'red',
              timeout: 3500,
              multiLine: false,
              icon: 'error'
            })
            console.log(error, 'error')
          } else {
            console.log(result, 'result')
            this.$q.notify({
              progress: true,
              message: 'Cadastrado com Sucesso',
              type: 'success',
              color: 'green',
              timeout: 3500,
              multiLine: false,
              icon: 'check'
            })
            this.buscarProdutos()
          }
          this.abrirPopupCadastro = false
          this.produto = {}
        })
    },

    verificarSeEdicao () {
      if (this.produto?._id) {
        this.editar(this.produto)
      } else {
        this.cadastrarProduto()
      }
    }
  }
}
