import {
  QList,
  QItem,
  QImg
} from 'quasar';
import VisualizarPedido from '/imports/ui/VisualizarPedido.vue'

export default {
  data() {
    return {
      abrirPopup: false,
      pedidos: [
        {
          fornecedor: 'Pet family',
          fotoProduto: '',
          id: 120,
          valor: 49.7,
          qtd: 22,
          dataHoraCompra: '17/11/2021 12:33',
          status: 'Em andamento',
          itens: [
            {
              produto: 'Ração Canina',
              valor: 45.5,
              qtd: 2
            },
            {
              produto: 'Shampoo Pet',
              valor: 15.2,
              qtd: 1
            },
            {
              produto: 'Escova Pet',
              valor: 10.9,
              qtd: 3
            },
            {
              produto: 'Creme dental',
              valor: 20.3,
              qtd: 1
            },
            {
              produto: 'Petisco Come Bem',
              valor: 5.3,
              qtd: 15
            }
          ]
        },
        {
          fornecedor: 'Pet Mais',
          fotoProduto: '',
          id: 119,
          valor: 89.7,
          qtd: 22,
          dataHoraCompra: '05/11/2021 16:40',
          status: 'Concluido',
          itens: [
            {
              produto: 'Escova Pet',
              valor: 10.9,
              qtd: 3
            },
            {
              produto: 'Creme dental',
              valor: 20.3,
              qtd: 1
            },
            {
              produto: 'Petisco Come Bem',
              valor: 5.3,
              qtd: 15
            }
          ]
        },
        {
          fornecedor: 'Pet Mais',
          fotoProduto: '',
          id: 118,
          valor: 89.7,
          qtd: 22,
          dataHoraCompra: '17/11/2021 12:33',
          status: 'Concluido',
          itens: [
            {
              produto: 'Escova Pet',
              valor: 10.9,
              qtd: 3
            },
            {
              produto: 'Creme dental',
              valor: 20.3,
              qtd: 1
            }
          ]
        },
        {
          fornecedor: 'Pet Mais',
          fotoProduto: '',
          id: 117,
          valor: 89.7,
          qtd: 22,
          dataHoraCompra: '01/11/2021 19:40',
          status: 'Concluido',
          itens: [
            {
              produto: 'Escova Pet',
              valor: 10.9,
              qtd: 1
            },
            {
              produto: 'Creme dental',
              valor: 20.3,
              qtd: 1
            }
          ]
        },
        {
          fornecedor: 'Tio dos Dogs',
          fotoProduto: '',
          id: 116,
          valor: 89.7,
          qtd: 22,
          dataHoraCompra: '05/10/2021 12:10',
          status: 'Concluido',
          itens: [
            {
              produto: 'Escova Pet',
              valor: 10.9,
              qtd: 3
            },
            {
              produto: 'Creme dental',
              valor: 20.3,
              qtd: 10
            }
          ]
        }
      ],
    }
  },
  components: {
    QList,
    QItem,
    QImg,
    VisualizarPedido
  },

  async created() {
    this.$q.loading.show()
    await this.carregarPedidos()
  },

  methods: {
    retornarTotal (pedido, i) {
      let total = 0
      pedido.itens.map((item) => {
        total += i === 'valor' ? item[i] * item['qtd'] : item[i]
      })
      return total
    },

    carregarPedidos () {
      Meteor.call('buscarTodosPedidos', (error,result)=>{
        if(error) {
          this.$q.notify({
            progress: true,
            message: error.reason,
            type: 'error',
            color: 'red',
            timeout: 3500,
            multiLine: false,
            icon: 'error'
          })
          console.log(error, 'error')
        } else {
          console.log(result, 'result')
          this.pedidos = result
        }
        this.$q.loading.hide()
        this.exibirTela = true
      })
    },
  }
}
